mapper.select:
SELECT * 
FROM (
SELECT rownum R, A.* 
	  FROM (
	  SELECT * FROM BOARD 
	  WHERE boardtype = '1' 
	  ORDER BY BOARDid DESC
	  )A
	  WHERE rownum <= #{endRow}
)
WHERE R >= #{startRow}

mapper.update:
UPDATE DATGLE 
SET CONTENT = #{content}
WHERE COMMENTNUM = #{commentnum}

	    UPDATE board
    	SET title = #{title}, content = #{content}
    	WHERE boardid = #{boardid}
